import {GET_ITEMS, VIEW_ITEM, VIEW_ITEM_CLOSE} from './types'
import axios from 'axios'

export const getItems = () => dispatch => {
        axios.get('https://the-wild-wind.herokuapp.com/api/items')
            .then(res => {
                const items = res.data
                dispatch({
                    type: GET_ITEMS,
                    payload: items
                })
            })
}

export const viewItem = id => dispatch => {
    axios.get('https://the-wild-wind.herokuapp.com/api/items/'+id)
        .then(res => {
            const item = res.data
            dispatch({
                type: VIEW_ITEM,
                payload: item,
            })
        })
}

export const viewItemClose = () => dispatch => {
    dispatch({
        type: VIEW_ITEM_CLOSE
    })
}
