import {SWITCH_ROUTE} from './types'

export const switchRoute = url => dispatch => {
    const headerText = {
        'news': 'Новости',
        'catalog': 'Каталог продукции',
        'about': 'О нас',
        'contacts': 'Контакты',
        'basket': 'Корзина',
        'landing': 'Здравствуйте',
    }
    dispatch({
        type: SWITCH_ROUTE,
        payload: headerText[url]
    })
}
