import React, { Component } from 'react'
import { connect } from 'react-redux'
import './basket-list.css'
import { removeFromBasket } from 'actions/itemActions'
import { bindActionCreators } from 'redux'

class List extends Component {
    render() {
        const items = this.props.basket.map(item => {
            return (
                <div className="basket-item" key={item.item.id}>
                    <div className="basket-item-image">
                        <img src={item.item.image} />
                    </div>
                    <div className="basket-item-header">{item.item.name}</div>
                    <div className="basket-item-count">{item.count}</div>
                    <div className="basket-item-buttons">
                        <button
                            onClick={() =>
                                this.props.removeFromBasket(item.item.id)
                            }
                        >
                            Удалить
                        </button>
                    </div>
                </div>
            )
        })
        return (
            <div className="basket-list">
                <div className="basket-item basket-header">
                    <div className="basket-item-image" />
                    <div className="basket-item-header">Наименование</div>
                    <div className="basket-item-count">Количество</div>
                    <div className="basket-item-buttons" />
                </div>
                {items}
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        basket: state.items.basket
    }
}
const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            removeFromBasket: removeFromBasket
        },
        dispatch
    )
}

export default connect(mapStateToProps, mapDispatchToProps)(List)
