import React, {Component} from 'react'
import {connect} from 'react-redux'
import Dropdown from 'components/dropdown'

class BasketShipping extends Component {
    render() {
        return (
            <div className="basket-shipping">
                <p>Адрес отправки:</p>
                <input type="text"/>
                <p>Способ доставки:</p>
                <Dropdown
                    src={this.props.shippingMethods}
                    title='Выберите службу доставки'
                />
            </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        shippingMethods: state.items.shippingMethods
    }
}

export default connect(
    mapStateToProps,
)(BasketShipping)
