import React, { Component } from 'react'
import { connect } from 'react-redux'
import Dropdown from 'components/dropdown'

class BasketShipping extends Component {
    render() {
        return (
            <div className="basket-shipping">
                <p className="label">Адрес отправки:</p>
                <input type="text" />
                <p className="label">Способ доставки:</p>
                <Dropdown
                    src={this.props.shippingMethods}
                    title="Выберите службу доставки"
                />
            </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        shippingMethods: state.items.shippingMethods
    }
}

export default connect(mapStateToProps)(BasketShipping)
