import React, { Component } from 'react'
import Menu from 'semantic-ui-react/dist/commonjs/collections/Menu'
import { NavLink } from 'react-router-dom'

export default class Navbar extends Component {
    componentDidMount() {}
    render() {
        return (
            <Menu pointing secondary>
                <Menu.Item
                    name="Новости"
                    as={NavLink}
                    exact
                    activeClassName="selected"
                    to="/news"
                    onClick={this.handleItemClick}
                />
                <Menu.Item
                    name="Каталог"
                    as={NavLink}
                    activeClassName="selected"
                    to="/catalog"
                    onClick={this.handleItemClick}
                />
                <Menu.Item
                    name="О нас"
                    as={NavLink}
                    activeClassName="selected"
                    to="/about"
                    onClick={this.handleItemClick}
                />
                <Menu.Item
                    name="Контакты"
                    as={NavLink}
                    activeClassName="selected"
                    to="/contacts"
                    onClick={this.handleItemClick}
                />
                <Menu.Menu position="right">
                    <Menu.Item
                        name="Корзина"
                        as={NavLink}
                        exact
                        activeClassName="selected"
                        to="/basket"
                        onClick={this.handleItemClick}
                    />
                    <Menu.Item
                        name="login"
                        as={NavLink}
                        exact
                        activeClassName="selected"
                        to="/news"
                        onClick={this.handleItemClick}
                    />
                    <Menu.Item
                        name="logout"
                        as={NavLink}
                        exact
                        activeClassName="selected"
                        to="/news"
                        onClick={this.handleItemClick}
                    />
                </Menu.Menu>
            </Menu>
        )
    }
}
