import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './landing.css'
import Responsive from 'semantic-ui-react/dist/commonjs/addons/Responsive'
import Button from 'semantic-ui-react/dist/commonjs/elements/Button'

class Landing extends Component {
    render() {
        return (
            <div className="ui container landing">
                <p className="header">Здравствуйте!</p>
                <Responsive className="langing-text">
                    Добро пожаловать в магазин экологичных товаров для дома
                </Responsive>
                <br />
                <Button as={Link} className="ui green button" to="/catalog">
                    Каталог товаров
                </Button>
            </div>
        )
    }
}

export default Landing
