import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './landing.scss'

class Landing extends Component {
    constructor(props) {
        super(props)
    }
    componentDidMount() {
        import('main')
    }
    render() {
        return (
            <div className="ui container landing">
                <p className="header">Здравствуйте!</p>
                <p className="langing-text">
                    Добро пожаловать в магазин экологичных товаров для дома
                </p>
                <br />
                <Link className="ui green button" to="/catalog">
                    Каталог товаров
                </Link>
            </div>
        )
    }
}

export default Landing
