import {GET_ITEMS, VIEW_ITEM, VIEW_ITEM_CLOSE} from '../actions/types'

const initialState = {
    list: [],
    viewItem: {},
    showModal: false,
}

export default function itemsReducer(state = initialState, action) {
    switch (action.type) {
        case GET_ITEMS:
            return {
                ...state,
                list: action.payload
            }
        case VIEW_ITEM:
            return {
                ...state,
                viewItem: action.payload,
                showModal: true,
            }
        case VIEW_ITEM_CLOSE:
            return {
                ...state,
                showModal: false,
            }
        default:
            return state
    }

}
