import {SWITCH_ROUTE} from '../actions/types'
const initialState = {
    headerText: 'Здравствуйте!'
}

export default function routesReducer(state=initialState,action) {
    switch (action.type) {
        case SWITCH_ROUTE:
            return {
                ...state,
                headerText: action.payload
            }
        default:
        return state
    }
}
